
import React, { useState, useEffect, useCallback, useRef } from 'react';

interface PomodoroTimerProps {
  isVisible: boolean;
  onClose: () => void;
}

const PomodoroTimer: React.FC<PomodoroTimerProps> = ({ isVisible, onClose }) => {
  // Timer logic state
  const [mode, setMode] = useState<'focus' | 'shortBreak' | 'longBreak'>('focus');
  const [timeRemaining, setTimeRemaining] = useState(25 * 60);
  const [isActive, setIsActive] = useState(false);
  const [cycles, setCycles] = useState(0);
  const [showSettings, setShowSettings] = useState(false);
  const [focusDuration, setFocusDuration] = useState(25);
  const [shortBreakDuration, setShortBreakDuration] = useState(5);
  const [longBreakDuration, setLongBreakDuration] = useState(30);
  const CYCLES_BEFORE_LONG_BREAK = 5;

  // Widget UI state
  const [isMinimized, setIsMinimized] = useState(false);
  const [position, setPosition] = useState({ x: window.innerWidth - 360, y: 80 });
  const [isDragging, setIsDragging] = useState(false);
  const dragStartPos = useRef({ x: 0, y: 0 });
  const widgetRef = useRef<HTMLDivElement>(null);

  // Use a valid, short, embedded audio file to prevent source errors
  const audioRef = useRef<HTMLAudioElement>(null);
  const audioDataUri = "data:audio/wav;base64,UklGRl9vT19XQVZFZm10IBAAAAABAAEAQB8AAEAfAAABAAgAZGF0YU=" + "_";

  const resetTimer = useCallback(() => {
    setIsActive(false);
    switch (mode) {
      case 'focus':
        setTimeRemaining(focusDuration * 60);
        break;
      case 'shortBreak':
        setTimeRemaining(shortBreakDuration * 60);
        break;
      case 'longBreak':
        setTimeRemaining(longBreakDuration * 60);
        break;
    }
  }, [mode, focusDuration, shortBreakDuration, longBreakDuration]);

  useEffect(() => {
    resetTimer();
  }, [mode, resetTimer]);

  useEffect(() => {
    let interval: NodeJS.Timeout | null = null;
    if (isActive && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining((time) => time - 1);
      }, 1000);
    } else if (isActive && timeRemaining === 0) {
      if (audioRef.current) {
        audioRef.current.play().catch(e => console.error("Audio play failed:", e));
      }
      setIsActive(false);
      if (mode === 'focus') {
        const newCycles = cycles + 1;
        setCycles(newCycles);
        if (newCycles % CYCLES_BEFORE_LONG_BREAK === 0) {
          setMode('longBreak');
        } else {
          setMode('shortBreak');
        }
      } else {
        setMode('focus');
      }
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isActive, timeRemaining, cycles, mode, CYCLES_BEFORE_LONG_BREAK]);

  const toggleTimer = () => setIsActive(!isActive);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };
  
  const getDuration = () => {
    switch (mode) {
        case 'focus': return focusDuration * 60;
        case 'shortBreak': return shortBreakDuration * 60;
        case 'longBreak': return longBreakDuration * 60;
        default: return focusDuration * 60;
    }
  }
  
  const handleDragStart = (e: React.MouseEvent<HTMLDivElement>) => {
    e.preventDefault();
    setIsDragging(true);
    dragStartPos.current = {
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    };
  };

  const handleDrag = useCallback((e: MouseEvent) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragStartPos.current.x,
        y: e.clientY - dragStartPos.current.y,
      });
    }
  }, [isDragging]);

  const handleDragEnd = useCallback(() => setIsDragging(false), []);

  useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleDrag);
      window.addEventListener('mouseup', handleDragEnd);
      window.addEventListener('mouseleave', handleDragEnd);
    }
    return () => {
      window.removeEventListener('mousemove', handleDrag);
      window.removeEventListener('mouseup', handleDragEnd);
      window.removeEventListener('mouseleave', handleDragEnd);
    };
  }, [isDragging, handleDrag, handleDragEnd]);

  if (!isVisible) {
    return null;
  }
  
  const durationInSeconds = getDuration();
  const progress = durationInSeconds > 0 ? ((durationInSeconds - timeRemaining) / durationInSeconds) * 100 : 0;
  
  const modeConfig = {
    focus: { label: 'Time to Focus!', color: 'text-sky-400' },
    shortBreak: { label: "Time for a short break!", color: 'text-green-400' },
    longBreak: { label: "Take a long break!", color: 'text-amber-400' },
  };

  const MinimizedView = () => (
    <div
      className="fixed z-50 bg-slate-800/90 backdrop-blur-sm border border-slate-600 rounded-full shadow-2xl flex items-center p-1 cursor-pointer"
      style={{ top: `${position.y}px`, left: `${position.x}px` }}
      onClick={() => setIsMinimized(false)}
      title="Click to expand"
      ref={widgetRef}
    >
      <div 
        onMouseDown={(e) => { e.stopPropagation(); handleDragStart(e); }}
        className="cursor-move p-2 rounded-full hover:bg-slate-700"
        title="Drag to move"
      >
          <span className="text-2xl" role="img" aria-label="Timer">üçÖ</span>
      </div>
      <span className="font-mono text-base text-white font-semibold mx-3 pointer-events-none">{formatTime(timeRemaining)}</span>
    </div>
  );

  if (isMinimized) {
    return <MinimizedView />;
  }
  
  return (
    <div
      ref={widgetRef}
      style={{ top: `${position.y}px`, left: `${position.x}px` }}
      className="fixed z-50 bg-slate-800/90 backdrop-blur-sm rounded-2xl shadow-2xl w-full max-w-xs border border-slate-700 text-white flex flex-col"
    >
      <header onMouseDown={handleDragStart} className="p-3 flex justify-between items-center border-b border-slate-700 cursor-move">
        <h2 className="text-md font-bold text-sky-400 flex items-center gap-2">
            <span className="text-xl" role="img" aria-label="Timer">üçÖ</span>
            Pomodoro Timer
        </h2>
        <div>
            <button onClick={() => setIsMinimized(true)} className="text-slate-400 hover:text-white mr-2" title="Minimize">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path d="M5 10a1 1 0 011-1h8a1 1 0 110 2H6a1 1 0 01-1-1z" /></svg>
            </button>
            <button onClick={onClose} className="text-slate-400 hover:text-white" title="Close">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>
            </button>
        </div>
      </header>

      <main className="p-4">
          <p className={`text-center font-semibold mb-4 ${modeConfig[mode].color}`}>
            {modeConfig[mode].label}
            {mode !== 'focus' && <span className="block text-xs text-slate-400 font-normal mt-1">Remember to drink water and take a walk!</span>}
          </p>

          <div className="relative w-40 h-40 mx-auto mb-4">
              <svg className="w-full h-full" viewBox="0 0 100 100">
                  <circle className="text-slate-700" strokeWidth="8" stroke="currentColor" fill="transparent" r="45" cx="50" cy="50" />
                  <circle className={`transform -rotate-90 origin-center ${modeConfig[mode].color} transition-all duration-500`} strokeWidth="8" strokeDasharray={2 * Math.PI * 45} strokeDashoffset={(2 * Math.PI * 45) - (progress / 100) * (2 * Math.PI * 45)} strokeLinecap="round" stroke="currentColor" fill="transparent" r="45" cx="50" cy="50" />
              </svg>
              <div className="absolute inset-0 flex flex-col items-center justify-center">
                  <span className="text-4xl font-bold tracking-tighter">{formatTime(timeRemaining)}</span>
              </div>
          </div>
          
          <div className="flex justify-center items-center gap-4 mb-4">
               <button onClick={toggleTimer} className={`w-28 px-4 py-2 text-lg font-bold rounded-lg transition-colors ${isActive ? 'bg-amber-500 hover:bg-amber-600' : 'bg-sky-500 hover:bg-sky-600'}`}>
                  {isActive ? 'Pause' : 'Start'}
              </button>
              <button onClick={() => resetTimer()} title="Reset Timer" className="p-3 bg-slate-700 rounded-full hover:bg-slate-600">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path fillRule="evenodd" d="M4 2a1 1 0 011 1v2.101a7.002 7.002 0 0111.898 2.162l-1.34.67a5.002 5.002 0 00-8.55-1.542A1 1 0 016.05 6H9a1 1 0 010 2H4a1 1 0 01-1-1V3a1 1 0 011-1zm10 15a1 1 0 01-1-1v-2.101a7.002 7.002 0 01-11.898-2.162l1.34-.67a5.002 5.002 0 008.55 1.542A1 1 0 0113.95 14H11a1 1 0 110-2h5a1 1 0 011 1v4a1 1 0 01-1 1z" clipRule="evenodd" /></svg>
              </button>
          </div>
          <div className="text-center text-xs text-slate-400">Cycles completed: {cycles}</div>

          <div className="mt-4 border-t border-slate-700 pt-3">
              <div className="flex justify-around items-center">
                  <span className="text-xs text-slate-400">Presets:</span>
                  <button onClick={() => setFocusDuration(25)} className={`text-xs px-2 py-1 rounded ${focusDuration === 25 ? 'bg-slate-600' : 'hover:bg-slate-700'}`}>25 min</button>
                  <button onClick={() => setFocusDuration(45)} className={`text-xs px-2 py-1 rounded ${focusDuration === 45 ? 'bg-slate-600' : 'hover:bg-slate-700'}`}>45 min</button>
                  <button onClick={() => setFocusDuration(60)} className={`text-xs px-2 py-1 rounded ${focusDuration === 60 ? 'bg-slate-600' : 'hover:bg-slate-700'}`}>60 min</button>
              </div>
          </div>
      </main>
      <audio ref={audioRef} src={audioDataUri} />
    </div>
  );
};

export default PomodoroTimer;
